## Real-World Code Example

```gts
import {
  CardDef,
  field,
  contains,
  linksTo,
  Component,
  getCardMeta,
  linksToMany,
} from 'https://cardstack.com/base/card-api';
import DatetimeField from 'https://cardstack.com/base/datetime';
import StringField from 'https://cardstack.com/base/string';
import MarkdownField from 'https://cardstack.com/base/markdown';
import NumberField from 'https://cardstack.com/base/number';

import { markdownToHtml } from '@cardstack/runtime-common';
import CalendarCog from '@cardstack/boxel-icons/calendar-cog';
import BlogIcon from '@cardstack/boxel-icons/notebook';

import { setBackgroundImage } from './components/layout';

import { Author } from './author';
import { FeaturedImageField } from './fields/featured-image';
import { formatDatetime, BlogApp as BlogAppCard } from './blog-app';
import { BlogCategory, categoryStyle } from './blog-category';
import { User } from './user';

class EmbeddedTemplate extends Component<typeof BlogPost> {
  <template>
    <article class='embedded-blog-post'>
      <div class='thumbnail' style={{setBackgroundImage @model.thumbnailURL}} />
      {{#if @model.categories.length}}
        <div class='categories'>
          {{#each @model.categories as |category|}}
            <div class='category' style={{categoryStyle category}}>
              {{category.shortName}}
            </div>
          {{/each}}
        </div>
      {{/if}}
      <h3 class='title'><@fields.title /></h3>
      <p class='description'>{{@model.description}}</p>
      <span class='byline'>
        {{@model.formattedAuthors}}
      </span>
      {{#if @model.datePublishedIsoTimestamp}}
        <time class='date' timestamp={{@model.datePublishedIsoTimestamp}}>
          {{@model.formattedDatePublished}}
        </time>
      {{/if}}
    </article>
    <style scoped>
      .embedded-blog-post {
        width: 100%;
        height: 100%;
        display: grid;
        grid-template:
          'img categories categories' max-content
          'img title title' max-content
          'img desc desc' max-content
          'img byline date' 1fr / 40% 1fr max-content;
        gap: var(--boxel-sp-xs);
        padding-right: var(--boxel-sp-xl);
        overflow: hidden;
      }
      .thumbnail {
        grid-area: img;
        background-color: var(--boxel-200);
        background-position: center;
        background-size: cover;
        background-repeat: no-repeat;
        margin-right: var(--boxel-sp-lg);
      }
      .title {
        grid-area: title;
        margin: var(--boxel-sp-xxs) 0 0;
        font-size: var(--boxel-font-size-lg);
        line-height: calc(30 / 22);
        letter-spacing: var(--boxel-lsp-xs);
      }
      .description {
        grid-area: desc;
        margin: 0;
        font-size: var(--boxel-font-size);
        line-height: calc(22 / 16);
        letter-spacing: var(--boxel-lsp-xs);
      }
      .byline {
        grid-area: byline;
        align-self: end;
        width: auto;
        height: auto;
        text-wrap: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
      }
      .date {
        grid-area: date;
        align-self: end;
        justify-self: end;
      }
      .byline,
      .date {
        margin-bottom: var(--boxel-sp-xs);
        font: 500 var(--boxel-font-sm);
        letter-spacing: var(--boxel-lsp-xs);
        text-wrap: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
      }

      .categories {
        margin-top: var(--boxel-sp);
        display: flex;
        flex-wrap: wrap;
        gap: var(--boxel-sp-xxxs);
      }

      .category {
        display: inline-block;
        padding: 3px var(--boxel-sp-xxxs);
        border-radius: var(--boxel-border-radius-sm);
        font: 500 var(--boxel-font-xs);
        letter-spacing: var(--boxel-lsp-sm);
      }
    </style>
  </template>
}

class FittedTemplate extends Component<typeof BlogPost> {
  <template>
    <article class='fitted-blog-post'>
      <div class='thumbnail' style={{setBackgroundImage @model.thumbnailURL}} />
      <div class='categories'>
        {{#each @model.categories as |category|}}
          <div class='category' style={{categoryStyle category}}>
            {{category.shortName}}
          </div>
        {{/each}}
      </div>
      <div class='content'>
        <h3 class='title'><@fields.title /></h3>
        <p class='description'>{{@model.description}}</p>
        {{#if @model.formattedAuthors}}
          <span class='byline'>{{@model.formattedAuthors}}</span>
        {{/if}}
        {{#if @model.datePublishedIsoTimestamp}}
          <time class='date' timestamp={{@model.datePublishedIsoTimestamp}}>
            {{@model.formattedDatePublished}}
          </time>
        {{/if}}
      </div>
    </article>
    <style scoped>
      .fitted-blog-post {
        width: 100%;
        height: 100%;
        min-width: 100px;
        min-height: 29px;
        display: grid;
        overflow: hidden;
      }
      .thumbnail {
        grid-area: img;
        background-color: var(--boxel-200);
        background-position: center;
        background-size: cover;
        background-repeat: no-repeat;
      }
      .content {
        grid-area: content;
        gap: var(--boxel-sp-4xs);
        padding: var(--boxel-sp-xs);
        overflow: hidden;
      }
      .title {
        grid-area: title;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 2;
        overflow: hidden;
        margin: 0;

        font: 600 var(--boxel-font-sm);
        letter-spacing: var(--boxel-lsp-sm);
        line-height: 1.3;
      }
      .description {
        grid-area: desc;
        display: -webkit-box;
        -webkit-box-orient: vertical;
        -webkit-line-clamp: 3;
        overflow: hidden;
        margin: 0;
        font: var(--boxel-font-xs);
        letter-spacing: var(--boxel-lsp-sm);
      }
      .byline {
        grid-area: byline;
        display: inline-block;
        text-wrap: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
      }
      .date {
        grid-area: date;
        text-wrap: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
      }
      .byline,
      .date {
        font: 500 var(--boxel-font-xs);
        letter-spacing: var(--boxel-lsp-sm);
      }

      .categories {
        margin-top: -27px;
        height: 20px;
        margin-left: 7px;
        display: none;
        overflow: hidden;
      }

      .category {
        height: 20px;
        padding: 3px 4px;
        border-radius: var(--boxel-border-radius-sm);
        display: inline-block;
        font: 500 var(--boxel-font-xs);
        letter-spacing: var(--boxel-lsp-sm);
        margin-right: var(--boxel-sp-xxxs);
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }

      @container fitted-card ((aspect-ratio <= 1.0) and (226px <= height)) {
        .fitted-blog-post {
          grid-template:
            'img' 42%
            'categories' max-content
            'content' 1fr / 1fr;
        }
        .categories {
          display: flex;
        }
        .content {
          display: grid;
          grid-template:
            'title title' max-content
            'desc desc' max-content
            'byline date' 1fr / 1fr max-content;
        }
        .byline,
        .date {
          align-self: end;
        }
        .date {
          justify-self: end;
        }
      }

      /* Aspect ratio < 1.0 (Vertical card) */
      @container fitted-card (aspect-ratio <= 1.0) and (224px <= height < 226px) {
        .fitted-blog-post {
          grid-template:
            'img' 92px
            'categories' max-content
            'content' 1fr / 1fr;
        }
        .categories {
          display: flex;
        }

        .content {
          display: grid;
          grid-template:
            'title' max-content
            'byline' max-content
            'date' 1fr / 1fr;
        }
        .description {
          display: none;
        }
        .date {
          align-self: end;
        }
      }

      @container fitted-card (aspect-ratio <= 1.0) and (180px <= height < 224px) {
        .fitted-blog-post {
          grid-template:
            'img' 92px
            'categories' max-content
            'content' 1fr / 1fr;
        }
        .categories {
          display: flex;
        }
        .content {
          display: grid;
          grid-template:
            'title' max-content
            'date' 1fr / 1fr;
        }
        .title {
          -webkit-line-clamp: 3;
        }
        .description,
        .byline {
          display: none;
        }
        .date {
          align-self: end;
        }
      }

      @container fitted-card ((aspect-ratio <= 1.0) and (height < 180px) ) {
        .title {
          font-size: var(--boxel-font-size-xs);
        }
      }

      @container fitted-card (aspect-ratio <= 1.0) and (148px <= height < 180px) {
        .fitted-blog-post {
          grid-template:
            'img' 80px
            'content' 1fr / 1fr;
        }
