          font-family: var(
            --blog-post-heading-font-family,
            'Playfair Display',
            serif
          );
        }
        h1 {
          font-size: 2.5rem;
          font-weight: 600;
          line-height: 1.25;
          letter-spacing: normal;
          margin-bottom: var(--boxel-sp-lg);
          margin-top: var(--boxel-sp-xl);
