import { CardDef, field, contains, Component } from 'https://cardstack.com/base/card-api'; // â½Â¹â¾ Core imports
import StringField from 'https://cardstack.com/base/string';
import NumberField from 'https://cardstack.com/base/number';
import CookingIcon from '@cardstack/boxel-icons/cooking-pot'; // â½Â²â¾ icon import

export class RecipeCard extends CardDef { // â½Â³â¾ Card definition
  static displayName = 'Recipe';
  static icon = CookingIcon;

  @field recipeName = contains(StringField); // â½â´â¾ Primary fields
  @field prepTime = contains(NumberField);
  @field cookTime = contains(NumberField);

