##### Advanced Formatting Patterns

```gts
// ‚ÅΩ¬π‚Åµ‚Åæ Conditional formatting based on value
get fileSizeDisplay() {
  const bytes = this.args.model?.fileSize;

  if (!bytes) return 'No file';

  // Show more precision for smaller files
  return formatFileSize(bytes, {
    binary: true,                    // Use KiB, MiB, etc.
    precision: bytes < 1024 ? 0 : 2, // "512 B" or "1.45 MiB"
    fallback: 'Unknown size'
  });
}

// ‚ÅΩ¬π‚Å∂‚Åæ Countdown with dynamic components
get eventCountdown() {
  const eventDate = this.args.model?.eventDate;
  const now = new Date();
  const msUntilEvent = new Date(eventDate).getTime() - now.getTime();
  const daysUntil = Math.floor(msUntilEvent / (1000 * 60 * 60 * 24));

  return formatCountdown(eventDate, {
    showDays: true,
    showHours: daysUntil < 7,     // Show hours in final week
    showMinutes: daysUntil < 1,   // Show minutes on final day
    showSeconds: daysUntil === 0, // Show seconds on event day
    fallback: 'Date not set'
  });
}

// ‚ÅΩ¬π‚Å∑‚Åæ Name formatting with cultural awareness
get authorDisplay() {
  const author = this.args.model?.author;

  return formatNames(author, {
    format: author?.culture === 'eastern' ? 'last-first' : 'first-last',
    includeMiddle: author?.showMiddleName !== false,
    separator: author?.culture === 'eastern' ? '' : ' ',
    locale: author?.locale || 'en-US',
    fallback: 'Anonymous'
  });
}

// ‚ÅΩ¬π‚Å∏‚Åæ Period formatting with ranges
get quarterDisplay() {
  const period = this.args.model?.fiscalQuarter; // "Q2 2024"

  const result = formatPeriod(period, {
    size: 'long',        // "Q2 2024 (Apr - Jun)"
    withRange: true,     // Returns object with date range
    fallback: 'No period'
  });

  // If withRange: { text: "Q2 2024 (Apr - Jun)", range: { start: Date, end: Date } }
  return typeof result === 'string' ? result : result.text;
}
