attributes.cssVariables:
:root {
  /* Base theme foundation */
  --background: oklch(0.08 0 0);              /* Rich charcoal base */
  --foreground: oklch(0.95 0 0);              /* Platinum white text */
  --card: oklch(0.12 0 0);                    /* Premium card surface */
  --card-foreground: oklch(0.95 0 0);         /* Card text */
  --popover: oklch(0.15 0 0);                 /* Elevated surfaces */
  --popover-foreground: oklch(0.95 0 0);      /* Popover text */
  --primary: oklch(0.88 0 0);                 /* Platinum silver primary */
  --primary-foreground: oklch(0.08 0 0);      /* Dark text on silver */
  --secondary: oklch(0.15 0 0);               /* Charcoal secondary */
  --secondary-foreground: oklch(0.95 0 0);    /* Light text on dark */
  --muted: oklch(0.25 0 0);                   /* Muted dark surfaces */
  --muted-foreground: oklch(0.70 0 0);        /* Muted text */
  --accent: oklch(0.75 0.15 65);              /* Electric gold accent */
  --accent-foreground: oklch(0.08 0 0);       /* Dark text on gold */
  --destructive: oklch(0.65 0.20 15);         /* Sophisticated red */
  --destructive-foreground: oklch(0.95 0 0);  /* Light text on red */
  --border: oklch(0.20 0 0);                  /* Subtle borders */
  --input: oklch(0.12 0 0);                   /* Input backgrounds */
  --ring: oklch(0.60 0.12 240);               /* Blue focus ring */

  /* Chart colors - Financial data visualization */
  --chart-1: oklch(0.75 0.15 65);             /* Gold primary */
  --chart-2: oklch(0.88 0 0);                 /* Platinum silver */
  --chart-3: oklch(0.60 0.12 240);            /* Professional blue */
  --chart-4: oklch(0.65 0.20 15);             /* Sophisticated red */
  --chart-5: oklch(0.70 0.10 160);            /* Muted green */

  /* Sidebar - Navigation and secondary content */
  --sidebar: oklch(0.10 0 0);                 /* Darker sidebar */
  --sidebar-foreground: oklch(0.95 0 0);      /* Sidebar text */
  --sidebar-primary: oklch(0.75 0.15 65);     /* Gold sidebar accent */
  --sidebar-primary-foreground: oklch(0.08 0 0); /* Dark text on gold */
  --sidebar-accent: oklch(0.15 0 0);          /* Sidebar accent surface */
  --sidebar-accent-foreground: oklch(0.95 0 0); /* Sidebar accent text */
  --sidebar-border: oklch(0.20 0 0);          /* Sidebar borders */
  --sidebar-ring: oklch(0.60 0.12 240);       /* Sidebar focus ring */

  /* Typography - Swiss precision with modern clarity */
  --font-sans: 'Inter', ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, 'Times New Roman', Times, serif;
  --font-mono: 'JetBrains Mono', ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace;

  /* Luxury-specific additions */
  --surface-premium: oklch(0.12 0 0);         /* Premium card surfaces */
  --surface-glass: oklch(0.15 0 0 / 0.8);     /* Glass overlay effects */
  --gold-accent: oklch(0.75 0.15 65);         /* Value highlights */
  --platinum-shimmer: oklch(0.92 0 0);        /* Shimmer effects */
  --value-positive: oklch(0.70 0.15 140);     /* Positive value green */
  --value-negative: oklch(0.65 0.20 15);      /* Negative value red */

  /* Spacing and geometry - Swiss grid precision */
  --spacing-unit: 8px;                        /* Base spacing unit */
  --radius: 0.375rem;                         /* Minimal, precise radius */

  /* Shadows - Premium depth and luxury */
  --shadow-luxury: 0 8px 32px oklch(0 0 0 / 0.4);     /* Deep luxury shadow */
  --shadow-card: 0 4px 16px oklch(0 0 0 / 0.3);       /* Card elevation */
  --shadow-subtle: 0 1px 4px oklch(0 0 0 / 0.2);      /* Subtle depth */
  --shadow-premium: 0 12px 48px oklch(0 0 0 / 0.5);   /* Premium elements */

  /* Standard shadows mapped to luxury variants */
  --shadow-2xs: 0 1px 3px 0px oklch(0 0 0 / 0.08);
  --shadow-xs: 0 1px 3px 0px oklch(0 0 0 / 0.10);
  --shadow-sm: 0 2px 6px 0px oklch(0 0 0 / 0.12), 0 1px 3px -1px oklch(0 0 0 / 0.12);
  --shadow: 0 2px 6px 0px oklch(0 0 0 / 0.15), 0 1px 3px -1px oklch(0 0 0 / 0.15);
  --shadow-md: 0 4px 12px 0px oklch(0 0 0 / 0.18), 0 2px 6px -1px oklch(0 0 0 / 0.18);
  --shadow-lg: 0 6px 20px 0px oklch(0 0 0 / 0.22), 0 4px 8px -1px oklch(0 0 0 / 0.22);
  --shadow-xl: 0 8px 32px 0px oklch(0 0 0 / 0.26), 0 8px 16px -1px oklch(0 0 0 / 0.26);
  --shadow-2xl: 0 12px 48px 0px oklch(0 0 0 / 0.35);

  /* Animation - Luxury timing and easing */
  --duration-micro: 120ms;                    /* Micro interactions */
  --duration-standard: 400ms;                 /* Standard transitions */
  --duration-slow: 600ms;                     /* Deliberate luxury timing */
  --easing-luxury: cubic-bezier(0.4, 0, 0.2, 1); /* Premium easing curve */
  --easing-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55); /* Subtle bounce */

  /* Additional utility variables */
  --tracking-normal: 0em;
  --tracking-wide: 0.025em;                   /* Swiss typography spacing */
  --tracking-wider: 0.05em;
}

/* Dark mode - Enhanced luxury */
.dark {
  --background: oklch(0.05 0 0);              /* Deeper black */
  --foreground: oklch(0.98 0 0);              /* Brighter platinum */
  --card: oklch(0.08 0 0);                    /* Darker cards */
  --card-foreground: oklch(0.98 0 0);         /* Brighter card text */
  --popover: oklch(0.12 0 0);                 /* Darker popovers */
  --popover-foreground: oklch(0.98 0 0);      /* Brighter popover text */
  --primary: oklch(0.92 0 0);                 /* Brighter platinum */
  --primary-foreground: oklch(0.05 0 0);      /* Deeper dark on platinum */
  --secondary: oklch(0.12 0 0);               /* Darker secondary */
  --secondary-foreground: oklch(0.98 0 0);    /* Brighter secondary text */
  --muted: oklch(0.18 0 0);                   /* Darker muted */
  --muted-foreground: oklch(0.75 0 0);        /* Brighter muted text */
  --accent: oklch(0.80 0.18 65);              /* Brighter gold */
  --accent-foreground: oklch(0.05 0 0);       /* Deeper dark on gold */
  --destructive: oklch(0.70 0.22 15);         /* Brighter red */
  --destructive-foreground: oklch(0.98 0 0);  /* Brighter text on red */
  --border: oklch(0.15 0 0);                  /* Darker borders */
  --input: oklch(0.08 0 0);                   /* Darker inputs */
  --ring: oklch(0.65 0.15 240);               /* Brighter focus ring */
}

/* Inline theme mapping for Tailwind compatibility */
@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  --font-sans: var(--font-sans);
  --font-mono: var(--font-mono);
  --font-serif: var(--font-serif);

  --radius-sm: calc(var(--radius) - 2px);
  --radius-md: var(--radius);
  --radius-lg: calc(var(--radius) + 2px);
  --radius-xl: calc(var(--radius) + 4px);

  --shadow-2xs: var(--shadow-2xs);
  --shadow-xs: var(--shadow-xs);
  --shadow-sm: var(--shadow-sm);
  --shadow: var(--shadow);
  --shadow-md: var(--shadow-md);
  --shadow-lg: var(--shadow-lg);
  --shadow-xl: var(--shadow-xl);
  --shadow-2xl: var(--shadow-2xl);
}
