## Site Navbar Component

**Component:** `SiteNavbar`

**Purpose:** Reusable navigation component that highlights active page based on parameter.

**Location:** `components/site-navbar.gts`

**Template Signature:**
```gts
interface SiteNavbarSignature {
  Args: {
    site: Site;                          // Site card with pages
    currentPageId: string;               // "product" | "docs" | "pricing" | "blog" | "community"
    sections?: PageSectionField[];       // Sections from current layout (for dropdown)
    isDarkMode?: boolean;                // Optional dark mode state
  };
}
```

export class SiteNavbar extends Component<SiteNavbarSignature> {
  @tracked openDropdownPageId: string | null = null;

  get visiblePages() {
    return this.args.site?.pages?.filter(p => p.showInNav) ?? [];
  }

  get sortedPages() {
    return this.visiblePages.sort((a, b) => 
      (a.navOrder ?? 0) - (b.navOrder ?? 0)
    );
  }

  get navSections() {
    return this.args.sections?.filter(s => s.showInNav) ?? [];
  }

  isActive = (pageId: string) => {
    return pageId === this.args.currentPageId;
  }

  toggleDropdown = (pageId: string) => {
    this.openDropdownPageId = this.openDropdownPageId === pageId ? null : pageId;
  }

  scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    this.openDropdownPageId = null;
  }
}

<template>
  <nav class="site-navbar {{if @isDarkMode 'dark-mode' ''}}">
    {{! Logo sourced from brand guide CSS variables }}
    <a href="/" class="navbar-logo">
