          {{! Dropdown menu for current page only }}
          {{#if (and page.hasDropdown (this.isActive page.pageId))}}
            <div class="nav-dropdown {{if (eq this.openDropdownPageId page.pageId) 'open' ''}}">
              {{#each this.navSections as |section|}}
