:root {
  /* == NEO-BRUTALIST THEME == */
  /* Raw HTML vibes, thick borders, mono text, no shadows, broken grid */

  /* BASE COLORS - High Contrast B&W */
  --background: oklch(1 0 0); /* Pure white */
  --foreground: oklch(0 0 0); /* Pure black */
  --card: oklch(1 0 0);
  --card-foreground: oklch(0 0 0);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0 0 0);

  /* PRIMARY - Solid black for brutal impact */
  --primary: oklch(0 0 0);
  --primary-foreground: oklch(1 0 0);

  /* SECONDARY - Harsh gray, no subtlety */
  --secondary: oklch(0.9 0 0);
  --secondary-foreground: oklch(0 0 0);

  /* MUTED - Low contrast gray */
  --muted: oklch(0.85 0 0);
  --muted-foreground: oklch(0.2 0 0);

  /* ACCENT - Same as secondary, no special treatment */
  --accent: oklch(0.9 0 0);
  --accent-foreground: oklch(0 0 0);

  /* DESTRUCTIVE - Keep harsh red but make it brutal */
  --destructive: oklch(0.5 0.3 27);
  --destructive-foreground: oklch(1 0 0);

  /* BORDERS - Thick black lines everywhere */
  --border: oklch(0 0 0);
  --input: oklch(0 0 0);
  --ring: oklch(0 0 0);

  /* CHARTS - Monochrome grayscale progression */
  --chart-1: oklch(0.2 0 0);
  --chart-2: oklch(0.4 0 0);
  --chart-3: oklch(0.6 0 0);
  --chart-4: oklch(0.8 0 0);
  --chart-5: oklch(0.95 0 0);

  /* SIDEBAR - Same brutal treatment */
  --sidebar: oklch(1 0 0);
  --sidebar-foreground: oklch(0 0 0);
  --sidebar-primary: oklch(0 0 0);
  --sidebar-primary-foreground: oklch(1 0 0);
  --sidebar-accent: oklch(0.9 0 0);
  --sidebar-accent-foreground: oklch(0 0 0);
  --sidebar-border: oklch(0 0 0);
  --sidebar-ring: oklch(0 0 0);

  /* TYPOGRAPHY - Monospace everything for 'raw HTML vibe' */
  --font-sans: 'Courier New', Courier, monospace;
  --font-serif: 'Courier New', Courier, monospace;
  --font-mono: 'Courier New', Courier, monospace;

  /* GEOMETRY - NO rounded corners, industrial sharp edges */
  --radius: 0px;

  /* SPACING - Tighter grid, less refined */
  --spacing: 0.125rem;

  /* TYPOGRAPHY TRACKING - Keep normal for monospace readability */
  --tracking-normal: 0em;

/* SHADOWS - ELIMINATE ALL DEPTH ILLUSION */
  --shadow-2xs: none;
  --shadow-xs: none;
  --shadow-sm: none;
  --shadow: none;
  --shadow-md: none;
  --shadow-lg: none;
  --shadow-xl: none;
  --shadow-2xl: none;
}

.dark {
  /* == DARK MODE BRUTALISM == */
  /* Inverted but equally harsh and unforgiving */

  /* BASE COLORS - Inverted B&W */
  --background: oklch(0 0 0);
  --foreground: oklch(1 0 0);
  --card: oklch(0 0 0);
  --card-foreground: oklch(1 0 0);
  --popover: oklch(0.1 0 0);
  --popover-foreground: oklch(1 0 0);

  /* PRIMARY - White on black for dark brutalism */
  --primary: oklch(1 0 0);
  --primary-foreground: oklch(0 0 0);

  /* SECONDARY - Dark gray, still harsh */
  --secondary: oklch(0.15 0 0);
  --secondary-foreground: oklch(1 0 0);

  /* MUTED - Dark muted, no subtlety */
  --muted: oklch(0.2 0 0);
  --muted-foreground: oklch(0.7 0 0);

  /* ACCENT - Same treatment */
  --accent: oklch(0.2 0 0);
  --accent-foreground: oklch(1 0 0);

  /* DESTRUCTIVE - Keep harsh but visible in dark */
  --destructive: oklch(0.6 0.25 27);
  --destructive-foreground: oklch(1 0 0);

  /* BORDERS - White lines on dark */
  --border: oklch(1 0 0);
  --input: oklch(1 0 0);
  --ring: oklch(1 0 0);

  /* CHARTS - Inverted grayscale */
  --chart-1: oklch(0.9 0 0);
  --chart-2: oklch(0.7 0 0);
  --chart-3: oklch(0.5 0 0);
  --chart-4: oklch(0.3 0 0);
  --chart-5: oklch(0.1 0 0);

  /* SIDEBAR - Dark brutal */
  --sidebar: oklch(0.05 0 0);
  --sidebar-foreground: oklch(1 0 0);
  --sidebar-primary: oklch(1 0 0);
  --sidebar-primary-foreground: oklch(0 0 0);
  --sidebar-accent: oklch(0.15 0 0);
  --sidebar-accent-foreground: oklch(1 0 0);
  --sidebar-border: oklch(0.3 0 0);
  --sidebar-ring: oklch(0.5 0 0);

  /* TYPOGRAPHY - Still monospace in dark */
  --font-sans: 'Courier New', Courier, monospace;
  --font-serif: 'Courier New', Courier, monospace;
  --font-mono: 'Courier New', Courier, monospace;

  /* GEOMETRY - Still no curves */
  --radius: 0px;

  /* SPACING - Still tight */
  --spacing: 0.125rem;

  /* TRACKING - Consistent */
  --tracking-normal: 0em;

  /* SHADOWS - STILL NONE */
  --shadow-2xs: none;
  --shadow-xs: none;
  --shadow-sm: none;
  --shadow: none;
  --shadow-md: none;
  --shadow-lg: none;
  --shadow-xl: none;
  --shadow-2xl: none;
}
